sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below

    // Remove caracteres não numéricos
    var cpf = newValue.replace(/[^\d]+/g,'');

    // Validação de tamanho
    if (cpf.length != 11) {
        g_form.showFieldMsg('cpf', 'CPF deve ter 11 dígitos.', 'error');
        return;
    }

    // Verifica CPFs inválidos conhecidos (todos dígitos iguais)
    if (/^(\d)\1+$/.test(cpf)) {
        g_form.showFieldMsg('cpf', 'CPF inválido.', 'error');
        return;
    }

    // Validação dos dígitos verificadores
    var soma = 0;
    var resto;

    for (var i = 1; i <= 9; i++)
        soma += parseInt(cpf.substring(i-1, i)) * (11 - i);
    resto = (soma * 10) % 11;
    if ((resto == 10) || (resto == 11)) resto = 0;
    if (resto != parseInt(cpf.substring(9, 10))) {
        g_form.showFieldMsg('cpf', 'CPF inválido.', 'error');
        return;
    }

    soma = 0;
    for (var j = 1; j <= 10; j++)
        soma += parseInt(cpf.substring(j-1, j)) * (12 - j);
    resto = (soma * 10) % 11;
    if ((resto == 10) || (resto == 11)) resto = 0;
    if (resto != parseInt(cpf.substring(10, 11))) {
        g_form.showFieldMsg('cpf', 'CPF inválido.', 'error');
        return;
    }

    // Se passou em tudo
    g_form.clearMessages();
    g_form.showFieldMsg('cpf', 'CPF válido.', 'info');
}
