sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue == '') return;

    // Regex do formato 000.000.000-00
    var regex = /^\d{3}\.\d{3}\.\d{3}\-\d{2}$/;
    if (!regex.test(newValue)) {
        g_form.showFieldMsg('cpf', 'Formato inválido. Preencha números e pontos conforme exemplo 000.000.000-00', 'error');
        return;
    }

    // Remove pontos e traço para validar matematicamente
    var cpf = newValue.replace(/[^\d]+/g,'');

    if (!validaCPF(cpf)) {
        g_form.showFieldMsg('cpf', 'CPF inválido', 'error');
    } else {
        g_form.hideFieldMsg('cpf', true);
    }
}

function validaCPF(cpf) {
    if (!cpf || cpf.length != 11 || /^(\d)\1+$/.test(cpf)) return false;

    var soma = 0, resto;

    // Valida 1º dígito verificador
    for (var i = 1; i <= 9; i++) soma += parseInt(cpf.substring(i-1, i)) * (11 - i);
    resto = (soma * 10) % 11;
    if ((resto == 10) || (resto == 11)) resto = 0;
    if (resto != parseInt(cpf.substring(9, 10))) return false;

    soma = 0;
    // Valida 2º dígito verificador
    for (i = 1; i <= 10; i++) soma += parseInt(cpf.substring(i-1, i)) * (12 - i);
    resto = (soma * 10) % 11;
    if ((resto == 10) || (resto == 11)) resto = 0;
    if (resto != parseInt(cpf.substring(10, 11))) return false;

    return true;
}
