sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Confere se é estagiário
    if (current.cargo == 'Estagiário') {

        // Busca cursos com status Aberto
        var cursos = new GlideRecord('x_967632_mock_skil_m2m_progresso');
        cursos.addQuery('status', 'Aberto');
        cursos.query();

        while (cursos.next()) {
            // Verifica se já existe relação (proteção contra duplicidade)
            var existente = new GlideRecord('x_967632_mock_skil_m2m_progresso');
            existente.addQuery('equipe', current.sys_id);
            existente.addQuery('curso', cursos.sys_id);
            existente.query();

            if (!existente.hasNext()) {
                var rel = new GlideRecord('x_967632_mock_skil_m2m_progresso');
                rel.initialize();
                rel.equipe = current.sys_id;
                rel.curso = cursos.sys_id;
                rel.progresso = 0; // começa do zero
                rel.insert();
            }
        }
    }
})(current, previous);
