sysauto_script.script:
(function() {
  try {
    // Executa o REST Message configurado
    var rm = new sn_ws.RESTMessageV2('Leitura dados tabela Cursos' ,'ListaCursos');
    var response = rm.execute();
    var status = response.getStatusCode();
    var body   = response.getBody();

    if (status !== 200) {
      gs.error('Leitura dados tabela Cursos: HTTP ' + status + ' — ' + body);
      return;
    }

    var data    = JSON.parse(body).result;
    var inserted = 0;

    for (var i = 0; i < data.length; i++) {
      var curso = data[i];

      // Evita duplicar pelo campo 'treinamento'
      var gr = new GlideRecord('x_967632_mock_skil_cursos');
      gr.addQuery('treinamento', curso.u_treinamento);
      gr.query();
      if (gr.next()) {
        continue;
      }

      // Insere novo registro
      gr.initialize();
      gr.template     = curso.u_template;
      gr.treinamento  = curso.u_treinamento;
      gr.duracao      = curso.u_duracao;  // sem acento
      gr.ordem        = curso.u_ordem;
      gr.ativo        = curso.u_ativo;
      gr.url          = curso.u_url;
      gr.insert();
      inserted++;
      gs.info('Leitura dados tabela Cursos: inserido → ' + curso.treinamento);
    }

    gs.info('Leitura dados tabela Cursos: Concluído. ' + inserted + ' registro(s) inserido(s).');
  }
  catch (ex) {
    gs.error('Leitura dados tabela Cursos: falha no REST — ' + ex.message);
  }
})();
